catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
		g_form.setMandatory('vs_platform',false);
		g_form.setVisible('vs_platform',false);
      return;
   }

	var system = g_form.getValue('vs_system');
	var gaFSMAjaxUtility = new GlideAjax('x_568222_fsm.FSMAjaxUtility');
	gaFSMAjaxUtility.addParam('sysparm_name','isDataAvailable');
	gaFSMAjaxUtility.addParam('sysparm_table','x_568222_fsm_system_platform');
	gaFSMAjaxUtility.addParam('sysparm_query','system=' +system);
	gaFSMAjaxUtility.addParam('sysparm_isNotNullQuery',true);
	gaFSMAjaxUtility.addParam('sysparm_notNullQueryField','platform');
	gaFSMAjaxUtility.getXML(evaluateDataAvailableResponse);

	function evaluateDataAvailableResponse(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.setMandatory('vs_platform', answer);
		g_form.setVisible('vs_platform', answer);
	}
   //Type appropriate comment here, and begin script below

}
