catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
      return;	  
   }	
   //Type appropriate comment here, and begin script below
    var system = g_form.getValue('vs_system');	
	if( system == "TM1" || system == "Anaplan"){
		g_form.clearOptions('benefit_classification');
		g_form.addOption('benefit_classification','Enhanced Planning Process','Enhanced Planning Process');
		g_form.addOption('benefit_classification','Embed Master Data Validation rules into ERP','Embed Master Data Validation rules into ERP');
		g_form.addOption('benefit_classification','Cleanse Finance Master Data to align with existing global standards','Cleanse Finance Master Data to align with existing global standards');
		g_form.addOption('benefit_classification','Optimize Job Scheduling','Optimize Job Scheduling');
	}
	else{		
		g_form.clearOptions('benefit_classification');
		g_form.addOption('benefit_classification','WWP or Compliance requirement','WWP or Compliance requirement');
		g_form.addOption('benefit_classification','Statutory requirement','Statutory requirement');
		g_form.addOption('benefit_classification','New process requirement','New process requirement');
		g_form.addOption('benefit_classification','Elimination of Manual Adjustment to BRAVO file','Elimination of Manual Adjustment to BRAVO file');
		g_form.addOption('benefit_classification','Automated Accuracy & Standardization','Automated Accuracy & Standardization');
		g_form.addOption('benefit_classification','Remove manual steps in process','Remove manual steps in process');
		g_form.addOption('benefit_classification','Improved Data Integrity','Improved Data Integrity');
		g_form.addOption('benefit_classification','Improved Data Latency','Improved Data Latency');				
	}
}
