var FSMAjaxUtility = Class.create();
FSMAjaxUtility.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{

	isDataAvailable: function() {
		// check if any data returns for this query
		// used in instances such as if a field should show on a record producer

		var table = this.getParameter("sysparm_table");
		var query = this.getParameter("sysparm_query");
		var _isNotNullQuery = this.getParameter("sysparm_isNotNullQuery") || undefined;
		var _notNullQueryField = this.getParameter("sysparm_notNullQueryField") || undefined;

		var dfitUtils = new x_568222_fsm.FSMUtils(); 
		return dfitUtils.isDataAvailable(table, query, _isNotNullQuery, _notNullQueryField);
	},
    type: 'FSMAjaxUtility'
});
