sys_script_include.script:
var FSMUtils = Class.create();
FSMUtils.prototype = {
    initialize: function() {
		this.gl = new global.GSLog('x_568222_fsm.gslog.fsmutils.log', "FSMUtils");
    },
isDataAvailable: function(table, query, _isNotNullQuery, _notNullQueryField) {
		// check if any data returns for this query
		// used in instances such as if a field should show on a record producer
		// used in determining if the current date falls in a close period

		//this.gl.logDebug("isDataAvailable: \ntable: " + table + "\nquery: " + query + "\n_isNotNullQuery: " + _isNotNullQuery + "_notNullQueryField: " + _notNullQueryField);

		var grDataAvailable = new GlideAggregate(table);
		grDataAvailable.addAggregate('COUNT');
		grDataAvailable.addEncodedQuery(query);
		if (_isNotNullQuery && _notNullQueryField) {
			grDataAvailable.addNotNullQuery(_notNullQueryField);
		}

		grDataAvailable.query();		
		if (grDataAvailable.next() && grDataAvailable.getAggregate('COUNT') > 0)   {
			this.gl.logDebug("isDataAvailable: \ntable: " + table + "\nquery: " + query + "\n_isNotNullQuery: " + _isNotNullQuery + "_notNullQueryField: " + _notNullQueryField + "\nreturn: true\ngrDataAvailable.getEncodedQuery: " + grDataAvailable.getEncodedQuery());
			return true;
		} else {
			this.gl.logDebug("isDataAvailable: \ntable: " + table + "\nquery: " + query + "\n_isNotNullQuery: " + _isNotNullQuery + "_notNullQueryField: " + _notNullQueryField + "\nreturn: false\ngrDataAvailable.getEncodedQuery: " + grDataAvailable.getEncodedQuery());
			return false;
		}
	},
    type: 'FSMUtils'
};
